void mostrarMesasResuelto(int indiceCorrecto, int mesaXpos[4], int mesaYpos[4]){
    int i;
    for (i=0; i<4;i++){
        if (i==indiceCorrecto){
            mostrarMesaCorrecta(indiceCorrecto,mesaXpos[i],mesaYpos[i]);
        }
        else {
            mostrarMesaIncorrecta(i,mesaXpos[i],mesaYpos[i]);
        }
    }
}

void quitarBilletes(int billetesMesa[4], int mesaXpos[4], int mesaYpos[4], int indCorrecta){
    int i;
    int j;
    for (i = 0; i<4;i++){
        if (i!=indCorrecta) {
            for (j = 1; j <= billetesMesa[i]; j++) {
                ocultarBillete(5 + j + (i * 10), mesaXpos[i] + (j * 2), mesaYpos[i] + (j * 2));
            }
        }
    }
}

void quitarBilletesCorrecta(int billetesMesa[4], int mesaXpos[4], int mesaYpos[4], int indCorrecta){
    int i;
    int j;
    for (i = 0; i<4;i++){
        if (i==indCorrecta) {
            for (j = 1; j <= billetesMesa[i]; j++) {
                ocultarBillete(5 + j + (i * 10), mesaXpos[i] + (j * 2), mesaYpos[i] + (j * 2));
            }
        }
    }
}

