void caerBilletes(int tick){
    oamRotateScale(&oamMain, 8, 1, 256 + 100 * tick * 2, 256 + 100 * tick * 2);
    oamUpdate(&oamMain);
}

void resetBilletes(){
    oamRotateScale(&oamMain, 8, 1, 256, 256);
    oamRotateScale(&oamMain, 9, 1, 256, 256);
    oamUpdate(&oamMain);
}








